package com.wsc201821;

public class _08 {
	public static void main(String[] args) {
        //字符串拼接+=
		// TODO Auto-generated method stub
		String a = "中华人民";
		String b = "共和国";
		String c = "我是" + a + b;
		String f="拉拉拉，";
		f+=c;
		System.out.println(c);
		System.out.println(f);
	}
}
